import React from 'react';
import womanWithGlass from '../assets/woman_with_glass.png';
import { motion } from "framer-motion";

const Hero = () => {
  return (
    <section id="accueil" className="bg-yellow-400 flex min-h-screen items-center justify-center py-20 px-4">
      <div className="bg-white bg-opacity-50 p-16 rounded-md shadow-lg flex items-center justify-between max-w-5xl w-full" style={{ borderRadius: '16px', paddingRight: '90px' }}>
        <div className="max-w-md px-8">
          {/* <motion.h1
            className="text-3xl md:text-5xl font-bold text-black mb-6"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          > */}
            Transformons votre événement en un souvenir mémorable.
          {/* </motion.h1> */}
          {/* <motion.p
            className="text-black text-sm mb-6"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.3 }}
          > */}
            Nous disposons d'une salle polyvalente pour accueillir vos anniversaires, mariages, baptêmes, et bien plus, le tout avec une capacité équivalente à 200 places. Nous disposons également d'un parking pouvant contenir jusqu'à 25 véhicules en moyenne.
          {/* </motion.p> */}
          <motion.button
            className="bg-black text-white px-6 py-3 rounded-md shadow-md hover:bg-gray-900"
            style={{ borderRadius: '14px' }}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            Réservez-maintenant
          </motion.button>
        </div>
        <div className="ml-8">
          <img src={womanWithGlass} alt="Femme avec verre" className="w-82 md:w-96 rounded-md" />
        </div>
      </div>
    </section>
  );
};

export default Hero;
